import { json } from 'react-router-dom';
import AuthForm from '../components/AuthForm';

function AuthenticationPage() {
  return <AuthForm />;
}

export default AuthenticationPage;

export async function action({request}) {
  const searchParams = new URL(request.url).searchParams; //this is how one get a hold of the request parameters when the router hook can't be used
  const mode = searchParams.get('mode') || 'login'; //if undefined, login is the default mode

  if(mode !== 'login' && mode !== 'signup') {
    throw json({message: 'Unsupported mode...'}, {status: 422});
  }

  const data = request.formData();
  const authData = {
    email: data.get('email'),
    password: data.get('password'),
  };

  const response = await fetch('http://localhost:8080/'+mode, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(authData)
  });
}
