import {createBrowserRouter, RouterProvider} from 'react-router-dom';
import HomePage from './pages/Home';
import EventsPage, {loader as eventLoader} from './pages/Events';
import EventDetailPage, {loader as eventDetailLoader, action as deleteEventAction} from './pages/EventDetail';
import NewEventPage from './pages/NewEvent';
import EditEventPage from './pages/EditEvent';
import RootPage from './pages/Root';
import EventsRootLayout from './pages/EventsRoot';
import ErrorPage from './pages/Error';
import {action as manipulateEventAction} from './components/EventForm';

const router = createBrowserRouter([
  {path: '/', 
   element: <RootPage />,
   errorElement: <ErrorPage />, 
   children: [
    {index: true, element: <HomePage />},
    {path: 'events', 
     element: <EventsRootLayout />, 
     children: [
      {index: true, element: <EventsPage />, loader: eventLoader}, //the loader function will be executed every time the user visit this route 
      {path: ':eventId',
       id: 'eventDetail', //this is to search for the router data in children routes
       loader: eventDetailLoader, //this route will share the loader function in two children routes
       children: [
        {index: true, element: <EventDetailPage />, action: deleteEventAction},
        {path: 'edit', element: <EditEventPage />, action: manipulateEventAction}
       ]
      },

      {path: 'new', element: <NewEventPage />, action: manipulateEventAction}, 
     ]},

  ]},

]);

function App() {
  return (
    <RouterProvider router={router}/>
  );
}

export default App;
